function jssubmit(Form, url, reload) {
	var options = {
		url : url,
		type : 'post',
		dataType : 'json',
		data : $(Form).serialize(),
		success : function(data) {
			$(data).each(function(i, v) {
				if (i == 0) {
					$(".backinfo").text(v);
				} else if (i == 1 && v != "null") {
					$("#btn").append("<button type='button' class='btn btn-info' onclick='download(\"" + v + "\")'>下载</button>");
				}
			});
		}
	};
	$.ajax(options);
	if (reload == 1) {
		location.reload();
	}
	return false;
}

function check_all(obj, cName) {
	var checkboxs = document.getElementsByName(cName);
	for (var i = 0; i < checkboxs.length; i++) {
		checkboxs[i].checked = obj.checked;
	}
}