function showPass(i) {
	$("#moadl1 p").text(i);
	$("#moadl1").modal('show');
}

function edit(url, id) {
	$.post(url, {eid : id}, function(data) {
		if (data != null) {
			$(data).each(function(i, v) {
				$("input[name='cid']").attr("value", v.id);
				$("input[name='username']").attr("placeholder", v.username);
				$("input[name='remarks']").attr("placeholder", v.remarks);
				$("input[name='money']").attr("placeholder", v.money/100);
				$("#moadl3 select option[value=" + v.group + "]").prop("selected", true);
				$("input[name='locked'][value='"+ v.locked +"']").prop("checked", "checked");
			});
		}
	}, "json");
	$("#moadl3").modal('show');
}

function add(flag) {
	if (flag == "admin") {
		$("#moadl2_1").modal('show');
	} else {
		$("#moadl2").modal('show');
	}
}

function deluser(demo, path) {
	swal({
		title : "您确定要删除这条信息吗",
		type : "warning",
		showCancelButton : true,
		confirmButtonColor : "#DD6B55",
		confirmButtonText : "是的，我要删除！",
		cancelButtonText : "让我再考虑一下…",
		closeOnConfirm : false,
		closeOnCancel : false
	}, function(isConfirm) {
		if (isConfirm) {
			$.post(path + "/Admin/delete/" + $(demo).attr("data-id"), function(data) {
				$(demo).parent().parent().remove();
			});
			swal("删除成功！");
			location.reload();
		} else {
			swal("已取消");
		}
	});
}